mapper.select:
SELECT
            id,
                    session_id,
                    user_message,
                    ai_response,
                    deepseek_token,
                    check_token,
                    creator,
                    updater,
                    create_time,
                    update_time,
                    deleted                FROM chat_record


                and id =
                #{queryBo.id}


                and session_id =
                #{queryBo.sessionId}



                and user_message like
                #{userMessageLikeValue}



                and ai_response like
                #{aiResponseLikeValue}


                and deepseek_token =
                #{queryBo.deepseekToken}


                and check_token =
                #{queryBo.checkToken}



                and creator like
                #{creatorLikeValue}



                and updater like
                #{updaterLikeValue}


                and create_time =
                #{queryBo.createTime}


                and update_time =
                #{queryBo.updateTime}


                and deleted =
                #{queryBo.deleted}
